semua_html = semua_html
  .replace(/(<div class="avatar-image-container")/g, "<section class='konten-komentar'>$1")
  .replace(/(<div class="comment-replies")/g, "</section>$1")
document.body.innerHTML = semua_html

const gambar_komentar = document.querySelectorAll('.avatar-image-container img')
gambar_komentar.forEach(x => x.src = x.src.replace('/s35/', '/s60/'))

const linknya = location.href
if (linknya.includes("?m=")) {
  const a = linknya.replace(/\?m=./, "")
  history.replaceState({}, document.title, a)
}
const store_bookmark = PetiteVue.reactive({
  bookmarked: !1,
  list_bookmark: [],
  cek() {
    if (localStorage.bookmark) {
      this.list_bookmark = JSON.parse(localStorage.bookmark)
      let o = !1
      for (var t of this.list_bookmark)
        t.link == location.href && (o = !0)
      this.bookmarked = o
    }
  },
  tambah() {
    ;(this.list_bookmark = [
      ...this.list_bookmark,
      { judul: document.title, link: location.href },
    ]),
      (this.list_bookmark = this.list_bookmark.sort((o, t) =>
        o.judul > t.judul ? 1 : -1
      )),
      (this.bookmarked = !0),
      (localStorage.bookmark = JSON.stringify(this.list_bookmark))
  },
  hapus() {
    ;(this.list_bookmark = this.list_bookmark.filter(
      (o) => o.link != location.href
    )),
      (this.bookmarked = !1),
      (localStorage.bookmark = JSON.stringify(this.list_bookmark))
  },
})
PetiteVue.createApp({ store_bookmark: store_bookmark }).mount(
  ".kontrol-bookmark.app"
)
PetiteVue.createApp({
  tampil_menu: !1,
  store_bookmark: store_bookmark,
  olah_menu(e) {
    e.innerText.match(/_.*/) &&
      (e.classList.add("anak"),
      (e.innerText = e.innerText.replace(/^_(.*)/, "$1")))
  },
  shortcut() {
    window.addEventListener("keyup", (e) => {
      "m" == e.key && 1 == e.altKey && (this.tampil_menu = !0),
        "Escape" == e.key && (this.tampil_menu = !1)
    })
  },
}).mount(".menu.app"),
  PetiteVue.createApp({
