      })
    },
  }).mount(".cari.app")

window.addEventListener("load", () => quicklink.listen());

function acak(t) {
  for (var o, a, e = t.length; 0 !== e; )
    (a = Math.floor(Math.random() * e)),
      (o = t[--e]),
      (t[e] = t[a]),
      (t[a] = o)
  return t
}

PetiteVue.createApp({
  random_posts: [],
  async dapatkan() {
    let o = await fetch("/feeds/posts/default?alt=json&max-results=0")
    ;(o = await o.json()), (o = o.feed.openSearch$totalResults.$t)
    let a = []
    for (let t = 1; t <= o; t++) a = [...a, t]
    a = acak(a).splice(0, 6)
    let e = []
    for (var r of a) {
      let t = await fetch(
        "/feeds/posts/default?alt=json&max-results=1&start-index=" + r
      )
      ;(t = await t.json()),
        (e = [
          ...e,
          {
            judul: t.feed.entry[0].title.$t,
            gambar: t.feed.entry[0].media$thumbnail.url.replace(
              "=s72-",
              "="
            ),
            link: t.feed.entry[0].link[4].href,
          },
        ])
    }
    this.random_posts = e
  },
}).mount(".random-posts.app")      
