PetiteVue.createApp({}).mount(".postingan.app");
PetiteVue.createApp({
  tampil_menu: !1,
  olah_menu(e) {
    e.innerText.match(/_.*/) &&
      (e.classList.add("anak"),
      (e.innerText = e.innerText.replace(/^_(.*)/, "$1")));
  },
}).mount(".menu.app"),
  PetiteVue.createApp({ tampil_cari: !1 }).mount(".cari.app");
(function () {
  window.addEventListener("load", () => {
    quicklink.listen();
  });

  const uri = window.location.toString();
  if (uri.indexOf("?m=1", "?m=1") > 0) {
    const clean_uri = uri.substring(0, uri.indexOf("?m=1"));
    window.history.replaceState({}, document.title, clean_uri);
  }
})();
